import React, { useState } from 'react';
import Container from '@material-ui/core/Container';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import RemoveIcon from '@material-ui/icons/Remove';
import AddIcon from '@material-ui/icons/Add';
import SendIcon from '@material-ui/core/Icon';


import { makeStyles } from '@material-ui/core/styles';
import { Icon } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    root: {
       '& .MuiTextField-root':{
           margin: theme.spacing(1),

       },
    },

    button: {
        margin: theme.spacing(1),
      }

}))


function Table() {  
    const classes = useStyles();
    const [inputFields, setInputFields] = useState([
        { type: '', lati: '', longi: '', address: ''},

    ]);

{/*To type*/}

    const handleChangeInput = (index, event) => {
        const values = [...inputFields];
        values[index][event.target.name] = event.target.value;
        setInputFields(values);
        }

{/*For console to indicate it works*/}

     const handleSubmit = (e) => {
         e.preventDefault();
         var AddRown = document.getElementById('show');
         var list1 = [];
		var list2 = [];
		var list3 = [];
		var list4 = [];

		var n = 1;
		var x = 0;

			var NewRow = AddRown.insertRow(n);

			list1[x] = document.getElementById("type").value;
			list2[x] = document.getElementById("lati").value;
			list3[x] = document.getElementById("longi").value;
			list4[x] = document.getElementById("address").value;

			var cel1 = NewRow.insertCell(0);
			var cel2 = NewRow.insertCell(1);
			var cel3 = NewRow.insertCell(2);
			var cel4 = NewRow.insertCell(3);

			cel1.innerHTML = list1[x];
			cel2.innerHTML = list2[x];
			cel3.innerHTML = list3[x];
			cel4.innerHTML = list4[x];

			n++;
			x++;
        };

{/*To add*/}

    const handleAddFields = () => {
        setInputFields([...inputFields, {type: '', lati: '', longi: '', address: '' }])
    }

{/*To remove*/}

    const handleRemoveFields = (index) => {
        const values  = [...inputFields];
        values.splice(index, 1);
        setInputFields(values);
    }



  return(

    <Container>

        <h1>Add New Locations</h1>

        <form className={classes.root} onSubmit={handleSubmit}>
            { inputFields.map((inputFields, index) => (
                <div key={index}>

                    <TextField
                       name= "type"
                       label= "Name"
                       variant='filled'
                       value={inputFields.type}
                       onChange={event => handleChangeInput(index, event)}
                    />

                    <TextField
                       name= "lati"
                       label= "Latitude"
                       variant='filled'
                       value={inputFields.lati}
                       onChange={event => handleChangeInput(index, event)}
                    />

                    <TextField
                       name= "longi"
                       label= "Longitude"
                       variant='filled'
                       value={inputFields.longi}
                       onChange={event => handleChangeInput(index, event)}
                    />

                    <TextField
                       name= "address"
                       label= "Address"
                       variant='filled'
                       value={inputFields.address}
                       onChange={event => handleChangeInput(index, event)}
                    />

                    <IconButton  onClick={handleAddFields}>
                        <AddIcon />
                    </IconButton>

                    <IconButton onClick={handleRemoveFields}>
                        <RemoveIcon />
                    </IconButton>

                </div>
            ))}

            <Button 
               className={classes.button}
               variant='contained'
               color='primary' 
               type='submit' 
               endIcon={<SendIcon>send</SendIcon>}
               onClick = {handleSubmit}
               >Send</Button>


        </form>
    </Container>    

  );

}


export default Table;
